function sendSailthru(forMattedData, onSuccess, onFail) {

  var stringify = JSON.stringify(forMattedData);
  console.log("callig endpoint...")
  fetch('https://a8dgwanwxa.execute-api.us-west-2.amazonaws.com/prod/sailthru/user', {
    mode: 'cors',method: 'post', body: stringify, headers: {"Content-type": "application/json"} })
  .then(
  function(response) {
    if (response.status !== 200) {
      onSuccess()
      return;
    }
    // Examine the text in the response
      onSuccess();
  })
  .catch(function(err) {
    onSuccess()
  });
}
function sendSailthruSync(forMattedData) {
  var xhr = new XMLHttpRequest();
  var url = 'https://a8dgwanwxa.execute-api.us-west-2.amazonaws.com/prod/sailthru/user';
  xhr.open('POST', url, false);
  xhr.setRequestHeader('Content-Type', 'application/json');
  xhr.send(JSON.stringify(
    forMattedData
  ));

  if (xhr.status >= 200 && xhr.status < 300) {
    return true;
  }

  return false;
}

